[(3.848e+33*cos(q3)^2 + 2.562e+34)/(4.548e+34*q2 + 3.77e+33*cos(q3) + 6.828e+33*q2*cos(q3)^2 + 1.887e+33*q2*cos(q3)^3 + 5.424e+33*cos(q3)^2 + 5.66e+32*cos(q3)^3 + 4.56e+32*cos(q3)^4 + 1.49e+34*q2^2*cos(q3)^2 + 1.257e+34*q2*cos(q3) + 9.923e+34*q2^2 + 1.59e+34),                                    (3.377e+34*q2 + 2.799e+33*cos(q3) + 3.794e+33*cos(q3)^2 + 9.33e+33*q2*cos(q3) + 7.368e+34*q2^2 + 2.206e+34)/(4.548e+34*q2 + 3.77e+33*cos(q3) + 6.828e+33*q2*cos(q3)^2 + 1.887e+33*q2*cos(q3)^3 + 5.424e+33*cos(q3)^2 + 5.66e+32*cos(q3)^3 + 4.56e+32*cos(q3)^4 + 1.49e+34*q2^2*cos(q3)^2 + 1.257e+34*q2*cos(q3) + 9.923e+34*q2^2 + 1.59e+34), (2.886e+33*sin(2.0*q3) + 1.162e+33*sin(3.0*q3) + 2.551e+34*sin(q3) + 1.519e+35*q2^2*sin(q3) + 9.619e+33*q2*sin(2.0*q3) + 6.962e+34*q2*sin(q3))/(4.548e+34*q2 + 3.77e+33*cos(q3) + 6.828e+33*q2*cos(q3)^2 + 1.887e+33*q2*cos(q3)^3 + 5.424e+33*cos(q3)^2 + 5.66e+32*cos(q3)^3 + 4.56e+32*cos(q3)^4 + 1.49e+34*q2^2*cos(q3)^2 + 1.257e+34*q2*cos(q3) + 9.923e+34*q2^2 + 1.59e+34)]
